con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);

ruTestdataArray = ["метсестра","агонь"];
enTestdataArray = ["firej","butterflai"];
ukTestdataArray = ["відстаньг","відчайь"];
ruHintsArray = ["медсестра","огонь"];
enHintsArray = ["fire","butterfly"];
ukHintsArray = ["відстань","відчай"];

def testStep = testRunner.testCase.getTestStepByName("sendCheckTexts");
def testCase = testRunner.testCase;
def testStepContext = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext(testStep);
def request = context.testCase.getTestStepByName("sendCheckTexts");

def counter = testRunner.testCase.getPropertyValue("counter").toInteger();

while (counter > 0) {
	counter -= 1;
	testCase.setPropertyValue("ruTestdata", ruTestdataArray[counter]);
	testCase.setPropertyValue("enTestdata", enTestdataArray[counter]);
	testCase.setPropertyValue("ukTestdata", ukTestdataArray[counter]);
	testRunner.runTestStepByName("sendCheckTexts");
	def responseCurrentHolder = groovyUtils.getXmlHolder(request.name +"#Response");
	responseCurrentHolder.namespaces["ns1"] = "http://speller.yandex.net/services/spellservice"
	def ruHintsResponse = responseCurrentHolder.getNodeValues("//ns1:SpellResult[1]//ns1:s");
	def enHintsResponse = responseCurrentHolder.getNodeValues("//ns1:SpellResult[2]//ns1:s");
	def ukHintsResponse = responseCurrentHolder.getNodeValues("//ns1:SpellResult[3]//ns1:s");

	if (ruHintsResponse[0] != ruHintsArray[counter] || 
	enHintsResponse[0] != enHintsArray[counter] || 
	ukHintsResponse[0] != ukHintsArray[counter]){
		testRunner.fail("Result not as expected! Expected: " + ruHintsArray[counter] + ", but found: " + ruHintsResponse[0]);
	}
}
